import sys
sys.setrecursionlimit(99999)

def maxAreaOfIsland(grid):
  visited = []
  maxA = 0
  directionMatrix = [[-1,0],[1,0],[0,-1],[0,1]]
  def dfs(grid,sr,sc,visited):
    visited.append([sr,sc])
    if grid[sr][sc] == 1:      
      localMax = 0
      for d in directionMatrix:
        nsr, nsc = sr+d[0],sc+d[1]
        if 0 <= nsr and nsr < len(grid) and 0 <= nsc and nsc <len(grid[nsr]) and [nsr,nsc] not in visited:
          localMax = localMax + dfs(grid,nsr,nsc,visited)
      return localMax + 1
    else:
      return 0
  for i in range(len(grid)):
    for j in range(len(grid[i])):
      maxA = max(maxA,dfs(grid,i,j,visited))
  return maxA
